import React, { useState, useEffect } from "react";

// Vite uses import.meta.env; CRA uses process.env
const API_KEY =
  import.meta.env.VITE_TMDB_API_KEY || process.env.REACT_APP_TMDB_API_KEY;

const MovieSearch = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  // Load from localStorage when component mounts
  useEffect(() => {
    const savedQuery = localStorage.getItem("movieQuery");
    const savedResults = localStorage.getItem("movieResults");
    if (savedQuery) setQuery(savedQuery);
    if (savedResults) setResults(JSON.parse(savedResults));
  }, []);

  // Save to localStorage whenever results or query change
  useEffect(() => {
    localStorage.setItem("movieQuery", query);
    localStorage.setItem("movieResults", JSON.stringify(results));
  }, [query, results]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    try {
      const res = await fetch(
        `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=${encodeURIComponent(
          query
        )}`
      );
      const data = await res.json();
      setResults(data.results || []);
    } catch (err) {
      console.error("Error fetching TMDB:", err);
    }
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Movie Search</h2>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          placeholder="Search for a movie..."
          onChange={(e) => setQuery(e.target.value)}
          style={{ padding: "0.5rem", width: "250px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          Search
        </button>
      </form>

      <div style={{ marginTop: "1rem" }}>
        {results.length === 0 ? (
          <p>No results yet.</p>
        ) : (
          results.map((movie) => (
            <div key={movie.id} style={{ marginBottom: "1rem" }}>
              <h3>{movie.title}</h3>
              {movie.poster_path && (
                <img
                  src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                  alt={movie.title}
                />
              )}
              <p>Release Date: {movie.release_date}</p>
              <p>{movie.overview}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default MovieSearch;
