import React, { useState, useEffect } from "react";

export default function MovieSearch() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  const searchMovies = async (e) => {
    e.preventDefault();
    const res = await fetch(
      `https://api.themoviedb.org/3/search/movie?api_key=YOUR_API_KEY&query=${query}`
    );
    const data = await res.json();
    setResults(data.results || []);
    localStorage.setItem("lastResults", JSON.stringify(data.results || []));
  };

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("lastResults"));
    if (stored) setResults(stored);
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Movie Search</h2>
      <form onSubmit={searchMovies} className="mb-4">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter a movie title"
          className="border p-2 mr-2"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2">
          Search
        </button>
      </form>

      <ul className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {results.map((movie) => (
          <li key={movie.id} className="p-4 shadow bg-white">
            <h3 className="font-semibold">{movie.title}</h3>
            {movie.poster_path && (
              <img
                src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                alt={movie.title}
              />
            )}
            <p>{movie.release_date}</p>
            <p className="text-sm">{movie.overview}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}
